-- ----------------------------
-- Procedure structure for backup_changzhou_tuoyuan_cash_flow
-- ----------------------------
DROP PROCEDURE IF EXISTS `backup_changzhou_tuoyuan_cash_flow`;
delimiter ;;
CREATE PROCEDURE `finance`.`backup_changzhou_tuoyuan_cash_flow`(IN backup_period VARCHAR(7))
BEGIN
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
    BEGIN
        ROLLBACK;
        RESIGNAL;
    END;
    
    START TRANSACTION;
    
    
    CREATE TABLE IF NOT EXISTS `changzhou_tuoyuan_cash_flow_backup` LIKE `changzhou_tuoyuan_cash_flow`;
    
    
    SELECT * FROM `changzhou_tuoyuan_cash_flow` 
    WHERE `period` = backup_period;
    
    COMMIT;
    
    SELECT CONCAT('Period ', backup_period, ' backed up successfully') AS message;
END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for cleanup_changzhou_tuoyuan_cash_flow
-- ----------------------------
DROP PROCEDURE IF EXISTS `cleanup_changzhou_tuoyuan_cash_flow`;
delimiter ;;
CREATE PROCEDURE `finance`.`cleanup_changzhou_tuoyuan_cash_flow`(IN cleanup_days INT)
BEGIN
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
    BEGIN
        ROLLBACK;
        RESIGNAL;
    END;
    
    START TRANSACTION;
    
    
    UPDATE `changzhou_tuoyuan_cash_flow`
    SET `status` = 0
    WHERE `created_at` < DATE_SUB(NOW(), INTERVAL cleanup_days DAY)
    AND `status` = 1;
    
    COMMIT;
    
    SELECT ROW_COUNT() AS affected_rows, 
           CONCAT('Data older than ', cleanup_days, ' days marked as deleted') AS message;
END
;;
delimiter ;

-- ----------------------------
-- Function structure for get_latest_changzhou_tuoyuan_period
-- ----------------------------
DROP FUNCTION IF EXISTS `get_latest_changzhou_tuoyuan_period`;
delimiter ;;
CREATE FUNCTION `finance`.`get_latest_changzhou_tuoyuan_period`()
 RETURNS varchar(7) CHARSET utf8mb4
  READS SQL DATA 
  DETERMINISTIC
BEGIN
    DECLARE latest_period VARCHAR(7);
    
    SELECT `period` INTO latest_period
    FROM `changzhou_tuoyuan_cash_flow`
    WHERE `status` = 1
    ORDER BY `period` DESC
    LIMIT 1;
    
    RETURN IFNULL(latest_period, '');
END
;;
delimiter ;

